    private void LoginButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_LoginButtonActionPerformed
        String username = libUser.getText();
        char [] pwdChar = libPass.getPassword();
        String pwd = new String(pwdChar);
        try{
            Connection connection = DBConnection.openConnection();
            String req = "SELECT * FROM utilisateur WHERE login = ? AND pwd = ? AND roles = 'EE'";
            PreparedStatement pstat = connection.prepareStatement(req);
            pstat.setString(1, username);
            pstat.setString(2, pwd);
            ResultSet res = pstat.executeQuery();      
            if (res.next()){
                EnsEtudInterface home = new EnsEtudInterface(new Utilisateur(
                        res.getString("id_utilisateur"),
                        res.getString("nom"),
                        res.getString("prenom"),
                        res.getString("login"),
                        res.getString("pwd"),
                        "Etudiant"
                ));
                home.setVisible(true);
                this.dispose();
                /*this.dispose();*/
                connection.close();
                pstat.close();
                res.close();
            }else{
                String invuserpd = "Connexion echou√©. Verifier vos informations!";
                invLog.setText(invuserpd);
                
                
            }
        }catch(Exception e){
            e.printStackTrace();
            System.out.println("Login Error");
        }
        
    }//GEN-LAST:event_LoginButtonActionPerformed

